/*Table structure for table `tb_orders` */

DROP TABLE IF EXISTS `tb_orders`;

CREATE TABLE `tb_orders` (
  `order_id` varchar(200) NOT NULL COMMENT '订单号',
  `total_price` decimal(10,0) DEFAULT NULL COMMENT '总价',
  `client_id` bigint(20) DEFAULT NULL COMMENT '卖家ID',
  `create_time` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `pay_time` timestamp NULL DEFAULT NULL COMMENT '支付时间',
  `cinema_id` varchar(222) DEFAULT NULL COMMENT '电影院Id',
  `status` bigint(20) DEFAULT '0' COMMENT '0:未支付 1已经支付2订单完成3已经退款',
  `spare` varchar(3000) DEFAULT NULL COMMENT '备选字段',
  PRIMARY KEY (`order_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

