/*Table structure for table `tb_payment_bill` */

DROP TABLE IF EXISTS `tb_payment_bill`;

CREATE TABLE `tb_payment_bill` (
  `order_id` varchar(200) NOT NULL COMMENT '订单号',
  `total_price` decimal(10,0) DEFAULT NULL COMMENT '总价',
