  `bill_type` bigint(55) DEFAULT NULL COMMENT '订单用途 1:购票 2:账户充值',
  `client_id` bigint(55) DEFAULT NULL COMMENT '客户ID',
  `payer` varchar(400) DEFAULT NULL COMMENT '支付人',
  `pay_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '支付时间',
  `spare` varchar(400) DEFAULT NULL COMMENT '备用',
  PRIMARY KEY (`order_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

